import React from "react";
import ReactDOM from "react-dom";

import "bootstrap/dist/css/bootstrap.min.css";
import { Button } from "reactstrap";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      gameID: null, 
      maxTries: 10,
      tries: 0,
      charCount: 0,
      firstChar: "w",
      lastChar: "d",
      current: "word",
      gameState: 2, // 1 = not started, 2 = started, 3 = won, 4 = lose
      wordState: "w**d" 
    };
  }

  render(){
    return (
      <div>
        <center>
            <h1>Wellcome to Hangout</h1>
            <p>You have {this.state.maxTries-this.state.tries} tries left</p>
            <p>{this.state.wordState}</p>

            {this.renderSwitch(this.state.gameState)}

            {
              this.state.gameState == 1 || this.state.gameState == 3 || this.state.gameState == 4
                ? (<Button onClick={() => this.startNewGame()}>Start new game</Button>)
                : this.props.textOrHtml
            }
        </center>
      </div>
    );
  };

  renderSwitch(param){
    switch(param) {
      case '3':
        return <h3>You WON</h3>;
      case '4':
        return <h3>You LOST</h3>;
      default:
        return this.props.textOrHtml;
    }
  }

  startNewGame(){
  };
};

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
